class Queue {
    constructor() {
        this.head = new Node();
        this.tail = this.head;
    }

    enqueue(elem) {
        const node = new Node(elem);
        if (this.isEmpty()) {
            this.head.next = node;
            this.tail = node;
        } else {
            this.tail.next = node;
            this.tail= node;
        }

    }

    dequeue() {
        if(this.isEmpty()){
            return -1;
        }else{
            let result = this.head.next.elem;
            this.head=this.head.next;

            return result;

        }


    }

    isEmpty(){
        if(!this.head.next){
            return true

        }else{
            return false
        }



    }




}


