import socket

def verify_certificate(hostname, port=443):
    context = ssl.create_default_context()
    with socket.create_connection((hostname, port)) as sock:
        with context.wrap_socket(sock, server_hostname=hostname) as ssock:
            cert = ssock.getpeercert()
            if not cert:
                raise ValueError("No certificate found")

            # Check for common name (CN)
            cn = next((v for k, v in cert['subject'][-1] if k == 'commonName'), None)
            if cn != hostname:
                raise ValueError(f"Common Name mismatch. Expected {hostname}, got {cn}")

            # Additional checks can be added here

            print("Certificate verified successfully.")

# Example usage
verify_certificate('example.com')
