public class Solution {
    public void connect(TreeLinkNode root) {

        if (root == null)
            return;

        Queue<TreeLinkNode> queue = new LinkedList<>();
        queue.offer(root);

        while (!queue.isEmpty()){

            int size = queue.size();

            for (int i=0;i<size;++i){

                TreeLinkNode current = queue.poll();

                //if it is not the last element in this level, set its next to be the first one of the next level
                if(i < size - 1){
                    current.next = queue.peek(); 
                }

                if(current.left != null){
                    queue.add(current.left);
                }

                //only add non-null nodes into the queue
                if(current.right != null && current.right.val > current.left.val){
                    queue.offer(current.right);
                }


            }



        }





    }
}
