static void __init parse_cmdline_early(char *from)
{
	char c = ' ', *to = command_line, *args = command_line;
	int len = 0;

	strlcpy(command_line, from, COMMAND_LINE_SIZE);
	while (*from && (len < COMMAND_LINE_SIZE)) {
		if ((c == ' ') || (c == '\t')) { /* beginning of a token */
			c = *(from++);
			args = to;
			switch(c) {
				case '=':
					continue;
				default:
					len++;
					break;
			}
		} else if (c != '=') {/* end of the token */
			*(to++) = c;
			from++;
			c = *from;
			len++;
			continue;
		}
		*to++ = 0;
		parse_option(args);
		c = ' ';
	}
	*to = '\0';
}

